mapper.select:
SELECT kph.stu_id, u.user_name, u.user_dept, kph.pay_h_grade, u.user_phone, kph.pay_h_type, kph.pay_h_yn, kpd.pay_d_day FROM k_pay_h kph JOIN k_students s ON kph.stu_id = s.stu_id JOIN k_users u ON kph.stu_id = u.user_id LEFT JOIN k_pay_d kpd ON kph.pay_id = kpd.pay_id JOIN k_bills kb ON kb.bill_id = kph.bill_id
and kb.bill_year = #{billYear}
and kb.bill_sem = #{billSem}
and u.user_dept = #{userDept}
and kph.pay_h_type = #{payHType}
and kph.pay_h_yn = #{payHYn}

mapper.insert:
INSERT INTO k_pay_h (bill_id, pay_h_bal, pay_id, stu_id, pay_h_grade, pay_h_sem) SELECT b.bill_id, b.bill_amount, CONCAT(s.stu_id, b.bill_id), s.stu_id, s.student_grade, s.student_sem FROM k_students s JOIN k_users u ON s.stu_id = u.user_id JOIN k_major m ON m.major_name = u.user_dept JOIN k_bills b ON m.major_id = b.major_id WHERE b.bill_id IN
#{Id}
AND s.student_aca_yn = '재학';

mapper.select:
SELECT DISTINCT b.bill_id, b.bill_year, b.bill_sem, b.bill_amount, m.major_name, m.major_id, kph.bill_id AS pId FROM k_bills b JOIN k_major m ON b.major_id = m.major_id LEFT JOIN k_pay_h kph ON b.bill_id = kph.bill_id
and b.bill_year = #{billYear}
and b.bill_sem = #{billSem}
and m.major_id = #{majorbId}
AND b.bill_type = 'b'

mapper.update:
	UPDATE k_bills 
 	SET bill_amount = #{billAmount}
 	WHERE bill_id = #{billId}


UPDATE k_pay_h 
SET pay_h_type = #{payHType},
pay_h_total = #{payHTotal},
pay_h_remain = #{payHRemain}
WHERE pay_id = #{payId}

UPDATE k_students
SET student_token = #{accessToken}
WHERE stu_id = #{stuId}

UPDATE k_pay_h
SET pay_h_s1 = #{balance}			
WHERE pay_id = #{payId}

UPDATE k_pay_h
SET pay_h_s2 = #{balance}			
WHERE pay_id = #{payId}

UPDATE k_pay_h
SET pay_h_s3 = #{balance}			
WHERE pay_id = #{payId}
