mapper.select:
select c.class_id, j.major_name, u.user_name, i.course_title, c.course_id, i.course_type, c.class_to, i.course_credit, c.class_syl
from k_class as c join k_class_member as m on c.class_id = m.class_id join k_course as i on i.course_id = c.course_id join k_users as u on u.user_id = c.prof_id join k_major as j on j.major_id = i.major_id
WHERE m.stu_id = #{userId}

mapper.update:

	UPDATE k_class
	 set class_ck = class_ck + 1

    WHERE class_id = #{classId}


UPDATE k_class 
 set class_ck = class_ck - 1 
WHERE class_id = #{classId}
