mapper.update:

UPDATE k_class_reg
 SET class_yn = 'y'
WHERE class_id = #{classId}

mapper.select:
SELECT c.class_id, j.major_name, u.user_name, co.course_title, c.course_id, co.course_grade, co.course_type, co.course_credit, c.class_sem, c.class_to, c.class_ck, c.class_syl, c.class_syl_original
from k_class as c join k_course as co on c.course_id = co.course_id join k_users as u on u.user_id = c.prof_id join k_major as j on j.major_id = co.major_id where class_id not in (SELECT class_id from k_class_member where stu_id =#{userId})
and j.major_name = #{majorName}
and co.course_grade = #{courseGrade}
order by class_id
