mapper.update:

UPDATE k_class_reg
 SET class_yn = 'y'
WHERE class_id = #{classId}

mapper.insert:
insert into k_class(
class_id, course_id, prof_id, class_to, class_year, class_sem, class_syl
)
select class_id, course_id, prof_id, class_to, class_year, class_sem, class_syl from k_class_reg
where class_id = #{classId}
