mapper.select:
SELECT h.class_id, h.quiz_h_title, h.quiz_h_content, h.quiz_h_file, h.quiz_h_id, h.quiz_h_seq, h.quiz_h_original,
d.quiz_d_date, d.quiz_d_openday, d.quiz_d_closeday, d.quiz_d_id, d.quiz_h_id,
cm.class_id FROM k_quiz_h as h JOIN k_quiz_d as d ON h.quiz_h_id = d.quiz_h_id JOIN k_class_member as cm ON h.class_id = cm.class_id WHERE cm.stu_id = #{userId}

SELECT r.quiz_r_content, r.quiz_r_date, r.quiz_r_file, r.quiz_r_orginal, r.quiz_r_id, r.quiz_d_id, r.class_member_id,
cm.class_member_id, cm.stu_id,
h.quiz_h_id, h.class_id FROM k_quiz_r as r JOIN k_class_member as cm ON r.class_member_id = cm.class_member_id JOIN k_quiz_d as d ON r.quiz_d_id = d.quiz_d_id JOIN k_quiz_h as h ON d.quiz_h_id = h.quiz_h_id WHERE r.quiz_d_id = #{quizDId} AND cm.stu_id = #{userId}

SELECT h.quiz_h_title, h.quiz_h_seq, h.class_id,
c.class_to,
d.quiz_d_date, d.quiz_d_id, NVL(d.quiz_d_cnt, 0) as 'quiz_d_cnt' FROM k_quiz_h as h JOIN k_class as c ON h.class_id = c.class_id JOIN k_quiz_d as d ON d.quiz_h_id = h.quiz_h_id WHERE c.prof_id = #{userId};

mapper.update:
		UPDATE
            k_quiz_r
        SET
            quiz_r_content = #{quizRContent}, quiz_r_file = #{quizRFile}, quiz_r_orginal = #{quizROrginal}
        WHERE
            quiz_r_id = #{quizRId};

UPDATE
	k_quiz_r
SET
	quiz_r_point = #{quizRPoint}
WHERE
	quiz_r_id = #{quizRId}
