		INSERT INTO k_quiz_h 
        (class_id, quiz_h_title, quiz_h_content, quiz_h_file, quiz_h_id, quiz_h_seq, quiz_h_original)
		SELECT class_id, #{quizHTitle}, #{quizHContent}, #{quizHFile}, #{quizHId}, NEXTVAL(quiz_seq), #{quizHOriginal}
        FROM k_class
        WHERE prof_id = #{userId} and class_id = #{classId}

	       INSERT INTO k_quiz_r
		(quiz_r_content, quiz_r_date, quiz_r_file, quiz_d_id, class_member_id, quiz_r_id, quiz_r_orginal)
        select #{quizRContent}, date_format(now(), '%Y%m%d'), #{quizRFile}, #{quizDId}, class_member_id, CONCAT("R", NEXTVAL(quiz_r_id_seq)), #{quizROriginal}
        from k_class_member
        where stu_id = #{userId} and class_id = #{classId}
