mapper.select:
select u.user_id, u.user_name, s.student_grade, s.student_sem, substr(u.user_ssn,1,6) user_birth, u.user_dept, if(u.user_sex = 'f','여','남') user_sex, s.student_aca_yn, u.user_nation, s.student_regdate, u.user_phone, s.student_graddate, u.user_email, c.credit_total, u.user_pic
from k_users u join k_students s on (u.user_id = s.stu_id) join k_credit c using (stu_id)
where u.user_id = #{param}

select u.user_name, u.user_id, u.user_dept, u.user_phone, sa.aca_id, sa.aca_type, sa.aca_start, ks.sch_title AS aca_start_t, ks.sch_start AS aca_start_s, ks.sch_end AS aca_start_e, sa.aca_end, ke.sch_title AS aca_end_t, ke.sch_start AS aca_end_s, ke.sch_end AS aca_end_e, sa.aca_reason, sa.aca_reason_d, sa.aca_yn, sa.aca_reject, sa.aca_date, sa.aca_file
from k_users u left join k_student_aca sa on (u.user_id = sa.stu_id) left join k_schedule ks on (ks.sch_id = sa.aca_start) left join k_schedule ke on (ke.sch_id = sa.aca_end)
where u.user_id = #{param} order by sa.aca_date desc
